  def valid_attributes
    {:name => "test1"}
  end

  describe "GET index" do
    it "assigns all tengine_job_expansions as @tengine_job_expansions" do
      Tengine::Job::Expansion.delete_all
      expansion = Tengine::Job::Expansion.create! valid_attributes
      get :index
      expansions = assigns(:expansions)
      expansions.to_a.should eq([expansion])
