    it "assigns the requested expansion as @expansion" do
      expansion = Tengine::Job::Expansion.create! valid_attributes
      get :edit, :id => expansion.id.to_s
      assigns(:expansion).should eq(expansion)
    end
  end

  describe "POST create" do
